import React, {useState} from "react";

const DataInput = () => {
    const [year, setYear] = useState(new Date().getFullYear());
    const [month,setMonth] = useState(new Date().getMonth() + 1);
    const [date,setDate] = useState(new Date().getDate());

    // input값이 변화하면 그 값을 바로 year로 바꾸어준다.

    // const handleChange = (e) => {
    //     setYear(e.target.value);
    // };
    // const handleMonthChange = (e) => {
    //     setMonth(e.target.value);
    // };
    // const handleDateChange = (e) => {
    //     setDate(e.target.value);
    // };

    const handleChange = (e, setState) => {
        setState(e.target.value);
    }

    // onChange, onClick, event 객체
    // click, change, keypress, scroll 등 
    // onKeyPress, onScroll

    return (
    <div id="dateInput">
        <button>오늘</button>
        <div>
            {/* <input type="text" value={year} onChange={handleChange} />년 
            <input type="text" value={month} onChange={handleMonthChange} />월
            <input type="text" value={day} onChange={handleDateChange} />일을 기준으로 */}
            <input type="text" value={year} onChange={(e) => handleChange(e,setYear)} />년 
            <input type="text" value={month} onChange={(e) => handleChange(e,setMonth)} />월
            <input type="text" value={date} onChange={(e) => handleChange(e,setDate)} />일을 기준으로
        </div>
    </div>
    );
};

export default DataInput;
